import React, {useState} from 'react';
import {Link} from '@reach/router';

const Form = () => {

    const [player, setPlayer] = useState({
        name: "", position: ""
    })
    const [errors, setErrors] = useState({
        name: "", position: ""
    })

    const handleChange = e =>{
        const curPlay = {
            ...player,
            [e.target.name]: e.target.value
        }
        validate(curPlay)
        setPlayer(curPlay)
    }

    const submitHandler = e =>{
        e.preventDefault();

    }

    const validate = pers =>{
        let valid = false;
        const {...curErr} = errors;
        if(pers.name.length === 0){
            curErr.name = "This field is required"
        } else if(pers.name.length < 3){
            curErr.name = "Name must be at least 3 characters"
        } else{
            curErr.name = ""
            valid = true;
        }

        if(pers.position.length < 2){
            curErr.position = "Position must be at least 2 characters"
        } else{
            curErr.position = ""
            valid = true;
        }

        setErrors(curErr)
        return valid
    }

    return (
        <div>

        </div>
    )
}

export default Form
